function extractResults() {
  var items = [];
  var results = document.querySelectorAll('div.result, div.result-op');
  var position = 1;
  results.forEach(function(node) {
    // 排除“大家还在搜”类推荐卡（不算结果），通常无 id / mu 属性、tpl=aladdin-san/app/recommend_list/result_bd1d926 等
    if (node.classList.contains('result') || node.classList.contains('result-op')) {
      // 过滤无关内容
      // 获取主跳转链接（通常为第一个大于 8 字的 a 标签）
      var a = node.querySelector('a[href^="http"],a[href^="/s?"],a[target="_blank"]');
      if (!a) return;
      var url = a.href;
      // 主标题（优先大号可点击 title/a，或 h3/t）
      var title = '';
      var h3 = node.querySelector('h3, .t, .cosc-title, [class*="title_"] a');
      if (h3 && h3.textContent.trim()) {
        title = h3.textContent.trim();
      } else if (a && a.textContent.length > 0) {
        title = a.textContent.trim();
      }
      if (!title) return;
      // 摘要
      var summary = '';
      var summaryNode = node.querySelector('.c-abstract, .summary-gap_3Jb4I, .cu-line-clamp-2, .cosd-markdown-content, .dqc2rye');
      if (summaryNode && summaryNode.textContent.trim().length > 0 && summaryNode.textContent.trim().length < 600) summary = summaryNode.textContent.trim();
      // 来源（如有）
      var source = '';
      var srcNode = node.querySelector('.cosc-source-text, .cosc-source-link, .source-name_6x7Ze');
      if (srcNode) source = srcNode.textContent.trim();
      // 广告标记
      var badges = [];
      if (node.innerHTML.indexOf('广告') > -1 || (a && a.innerHTML.indexOf('广告') > -1)) badges.push('广告');
      // 发布时间
      var publishedAt = '';
      // 查找如“2026-01”、“yyyy-mm-dd”短日期
      var dtMatch = node.innerText.match(/(20[12][0-9][.-][01]?\d[.-][0-3]?\d)/);
      if (dtMatch) publishedAt = dtMatch[1].replace(/[.-]/g,'-');
      items.push({
        id: node.id||'',
        url,
        title,
        summary,
        source,
        publishedAt,
        position: position++,
        badges
      });
    }
  });
  return items;
}
